using UnityEngine;using System.Collections;public class ThrowableController : MonoBehaviour {    private bool thrown;	// Use this for initialization	void Start () {        thrown = false;	}		// Update is called once per frame	void Update () {		}    void FixedUpdate()    {        bool fire = Input.GetButtonDown("Jump");        if (fire)        {            thrown = true;            rigidbody.AddForce(new Vector3(0, 450, 1000));        }    }}